import React from 'react';
import axios from 'axios';
const Welcome = ({ user, onSignOut }) => {
    return (
        <div>
            Bienvenida a mi red social <strong>{user.username}</strong>!
        <a href="javascript:;" onClick={onSignOut}>Sign out</a>
        </div>
    )
}

class TextField extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            text: '',
            type: ''
        }
    }
    render() {
        return (
            <div>
                <input></input>
            </div>
        )
    }
}
class Button extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        }
    }
    render() {
        return (
            <div>
                <button>Login</button>
            </div>
        )
    }
}
class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: ''
        }
        this.handleClick = this.handleClick.bind(this)
    }
    handleSignIn(event) {
        event.preventDefault()
        let username = this.refs.username.value
        let password = this.refs.password.value
        this.props.onSignIn(username, password)
        // encrypt the password here?
        // send the data to check in the backend
    }
    handleClick(event) {
        let apiBaseUrl = "http://localhost:4000/api/";
        // carga util, osea datos que vamos a enviar
        let payload = {
            "email": this.state.username,
            "password": this.state.password
        }
        axios.post(apiBaseUrl + 'login', payload)
            .then(function (response) {
                console.log(response);
                if (response.data.code == 200) {
                    console.log("Login successfull");
                }
                else if (response.data.code == 204) {
                    console.log("Username password do not match");
                    alert("username password do not match") // change this
                }
                else {
                    console.log("Username does not exists");
                    alert("Username does not exist"); // change this
                }
            })
            .catch(function (error) {
                console.log(error);
            });
    }
    render() {
        return (
            <form onSubmit={this.handleSignIn.bind(this)}>
                <h1>Login</h1>
                <TextField
                    onChange={(event, newValue) => this.setState({ username: newValue })}
                />
                <br />
                <TextField
                    type="password"
                    onChange={(event, newValue) => this.setState({ password: newValue })}
                />
                <br />
                <input type="submit" value="Login" />
            </form>
        );
    }
}
const style = {
    margin: 15,
};
export default Login;
